import React, { Component } from 'react';
import { Container, Form } from './style'
import Header from '../../components/Header'
import SideDrawer from '../../components/SideDrawer/index'
import FundoEscuro from '../../components/fundoEscuro/index'
import 'semantic-ui-css/semantic.min.css'

class Cadastro extends Component {
  state = {
    abrirMenu: false
  }


  abrirMenu = () => {
    this.setState((prevState) => {
      return { abrirMenu: !prevState.abrirMenu }
    })
  }

  fecharMenu = () => {
    this.setState({ abrirMenu: false })
  }

  render() {

    let fundoEscuro;

    if (this.state.abrirMenu) {

      fundoEscuro = <FundoEscuro click={this.fecharMenu} />
    }
    return (
      <Container>
        <Header abrir={this.abrirMenu} />
        <SideDrawer show={this.state.abrirMenu} />
        {fundoEscuro}

        <Form>
          <Form>
            <Form.Group widths='equal'>
              <Form.Field
                id='form-input-control-first-name'
                control={Input}
                label='First name'
                placeholder='First name'
              />
              <Form.Field
                id='form-input-control-last-name'
                control={Input}
                label='Last name'
                placeholder='Last name'
              />
              <Form.Field
                control={Select}
                options={genderOptions}
                label={{ children: 'Gender', htmlFor: 'form-select-control-gender' }}
                placeholder='Gender'
                search
                searchInput={{ id: 'form-select-control-gender' }}
              />
            </Form.Group>
            <Form.Field
              id='form-textarea-control-opinion'
              control={TextArea}
              label='Opinion'
              placeholder='Opinion'
            />
            <Form.Field
              id='form-input-control-error-email'
              control={Input}
              label='Email'
              placeholder='joe@schmoe.com'
              error={{
                content: 'Please enter a valid email address',
                pointing: 'below',
              }}
            />
            <Form.Field
              id='form-button-control-public'
              control={Button}
              content='Confirm'
              label='Label with htmlFor'
            />
          </Form>
        </Form>


      </Container>
    );
  }
}

export default Cadastro;
