import React, { Component } from 'react';
import { Container, ContainerCentro } from '../home/styled'
import Header from '../../components/Header'
import SideDrawer from '../../components/SideDrawer/index'
import FundoEscuro from '../../components/fundoEscuro/index'
import { Grid, Image } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css'

class Home extends Component {
  state = {
    abrirMenu: false
  }


  abrirMenu = () => {
    this.setState((prevState) => {
      return { abrirMenu: !prevState.abrirMenu }
    })
  }

  fecharMenu = () => {
    this.setState({ abrirMenu: false })
  }

  render() {

    let fundoEscuro;

    if (this.state.abrirMenu) {

      fundoEscuro = <FundoEscuro click={this.fecharMenu} />
    }
    return (
      <Container>
        <Header abrir={this.abrirMenu} />
        <SideDrawer show={this.state.abrirMenu} />
        {fundoEscuro}

        <ContainerCentro>
          <Grid celled>
            <Grid.Row>
              <Grid.Column width={3}>
                <Image src='https://lh3.googleusercontent.com/GPC-DPV91Mp3j7-eFT8Tcs1DG9nZt3QZg7aXbYlhpuX4ec3U58UE8EcmUbCf1ZmnEc8=s180-rw' />
              </Grid.Column>
              <Grid.Column width={13}>
                <Image src='/images/wireframe/centered-paragraph.png' />
              </Grid.Column>
            </Grid.Row>

            <Grid.Row>
              <Grid.Column width={3}>
                <Image src='/images/wireframe/image.png' />
              </Grid.Column>
              <Grid.Column width={10}>
                <Image src='/images/wireframe/paragraph.png' />
              </Grid.Column>
              <Grid.Column width={3}>
                <Image src='/images/wireframe/image.png' />
              </Grid.Column>
            </Grid.Row>
          </Grid>

        </ContainerCentro>


      </Container>
    );
  }
}

export default Home;
