import React, { Component } from 'react';
import { Container, Grid } from './styled'
import Header from '../../components/Header'
import SideDrawer from '../../components/SideDrawer/index'
import FundoEscuro from '../../components/fundoEscuro/index'
import { CrudFuncionarios, CrudNovoFuncionarios } from '../../components/crudFuncionarios'
import { Button, Card, Divider, Image, Placeholder } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css'

class ConfigFuncionarios extends Component {
  state = {
    funcionarios: [{
      Nome: "Marcos Paulo",
      Funcao: "Comercial",
      horasTrabalhada: "10",
      BancoHoras: "5"
    },
    {
      Nome: "Wagner Rocha",
      Funcao: "Comercial",
      horasTrabalhada: "10",
      BancoHoras: "5"
    },
    {
      Nome: "Marcos Paulo",
      Funcao: "Comercial",
      horasTrabalhada: "10",
      BancoHoras: "5"
    },
    {
      Nome: "Marcos Paulo",
      Funcao: "Comercial",
      horasTrabalhada: "10",
      BancoHoras: "5"
    },
    {
      Nome: "Marcos Paulo",
      Funcao: "Comercial",
      horasTrabalhada: "10",
      BancoHoras: "5"
    }],
    abrirMenu: false,
    loading: true
  }

  abrirMenu = () => {
    this.setState((prevState) => {
      return { abrirMenu: !prevState.abrirMenu }
    })
  }

  fecharMenu = () => {
    this.setState({ abrirMenu: false })
  }


  handleLoadingClick = () => {
    this.setState({ loading: true })

    setTimeout(() => {
      this.setState({ loading: false })
    }, 3000)
  }

  render() {
    const { funcionarios } = this.state
    const { loading } = this.state
    let fundoEscuro;

    if (this.state.abrirMenu) {

      fundoEscuro = <FundoEscuro click={this.fecharMenu} />
    }
    return (
      <Container>
        <Header abrir={this.abrirMenu} />
        <SideDrawer show={this.state.abrirMenu} />
        {fundoEscuro}

        <Card.Group doubling itemsPerRow={3} stackable>
          {funcionarios.map(funcionarios => (
            <Card key={funcionario.Nome}>
              {loading ? (
                <Placeholder>
                  <Placeholder.Image square />
                </Placeholder>
              ) : (
                  <Image src={funcionario.avatar} />
                )}

              <Card.Content>
                {loading ? (
                  <Placeholder>
                    <Placeholder.Header>
                      <Placeholder.Line length='very short' />
                      <Placeholder.Line length='medium' />
                    </Placeholder.Header>
                    <Placeholder.Paragraph>
                      <Placeholder.Line length='short' />
                    </Placeholder.Paragraph>
                  </Placeholder>
                ) : (
                    <>
                      <Grid>
                        {funcionarios.map(funcionario =>
                          <CrudFuncionarios Nome={funcionario.Nome} Funcao={funcionario.Funcao} horasTrabalhada={funcionario.horasTrabalhada} BancoHoras={funcionario.BancoHoras} />
                        )}
                        <CrudNovoFuncionarios />
                      </Grid>
                    </>
                  )}
              </Card.Content>

              <Card.Content extra>
                <Button disabled={loading} primary>
                  Add
                </Button>
                <Button disabled={loading}>Delete</Button>
              </Card.Content>
            </Card>
          ))}
        </Card.Group>





      </Container>
    );
  }
}

export default ConfigFuncionarios;
